def return_categories_from_key_list(key_list):
	keys_categories = {
    'W':        [1,0,0,0,0,0,0],
    'A':        [0,1,0,0,0,0,0],
    'S':        [0,0,1,0,0,0,0],
    'D':        [0,0,0,1,0,0,0],
    'SPACE':    [0,0,0,0,1,0,0],
    'E':        [0,0,0,0,0,1,0],
    'SHIFT':    [0,0,0,0,0,0,1],
    'NOTHING':  [0,0,0,0,0,0,0],
	} 
	category_list = []

	for keys in key_list:
		if type(keys) is str:
			category_list.append(keys_categories[keys])
		if type(keys) is list:
			category_list.append(connection_two_categories(keys_categories[keys[0]],keys_categories[keys[1]]))
	return category_list


def connection_two_categories(category_one, category_two):
	connected = [num for num in category_one]


	for element in category_two:

		if element == 1:
			connected.insert(category_two.index(element), 1)
			connected.pop()
			break
	return connected

def find_usage_key(category_list, finding_key):
	keys_categories = {
	'W':        [1,0,0,0,0,0,0],
	'A':        [0,1,0,0,0,0,0],
	'S':        [0,0,1,0,0,0,0],
	'D':        [0,0,0,1,0,0,0],
	'SPACE':    [0,0,0,0,1,0,0],
	'E':        [0,0,0,0,0,1,0],
	'SHIFT':    [0,0,0,0,0,0,1],
	'NOTHING':  [0,0,0,0,0,0,0],
	} 

	finding_key = keys_categories[finding_key]
	positions = []

	for num in finding_key:
		if num == 1:
			target_position = finding_key.index(num)

	counter = -1 
	counter2 = -1 

	for category in category_list:
		counter2 += 1
		for num in category:
			counter += 1
			if counter == target_position and num == 1:
				positions.append(counter2)
		counter = -1
	return positions








